    {"Equipment ID":"EQX1025","Type":"Loader","Site ID":"S002","Check-Out Date":"2025-01-09","Check-In Date":"2025-02-11","Engine Hours/Day":"6.7","Idle Hours/Day":"6.0","Operating Days":"5","Last Operator ID":"OP105","FuelUsage(L/day)":"24.9"},
    {"Equipment ID":"EQX1026","Type":"Dump Truck","Site ID":"S003","Check-Out Date":"2025-01-19","Check-In Date":"2025-02-20","Engine Hours/Day":"1.9","Idle Hours/Day":"9.8","Operating Days":"13","Last Operator ID":"OP110","FuelUsage(L/day)":"37.8"},
    {"Equipment ID":"EQX1027","Type":"Grader","Site ID":"S008","Check-Out Date":"2025-03-03","Check-In Date":"2025-04-14","Engine Hours/Day":"7.3","Idle Hours/Day":"3.7","Operating Days":"11","Last Operator ID":"OP102","FuelUsage(L/day)":"17.1"},
    {"Equipment ID":"EQX1028","Type":"Excavator","Site ID":"S008","Check-Out Date":"2025-05-04","Check-In Date":"2025-06-09","Engine Hours/Day":"3.7","Idle Hours/Day":"11.4","Operating Days":"40","Last Operator ID":"OP102","FuelUsage(L/day)":"13.7"},
    {"Equipment ID":"EQX1029","Type":"Crane","Site ID":"S009","Check-Out Date":"2025-01-05","Check-In Date":"2025-02-21","Engine Hours/Day":"6.2","Idle Hours/Day":"10.1","Operating Days":"38","Last Operator ID":"OP108","FuelUsage(L/day)":"19.8"},
    {"Equipment ID":"EQX1030","Type":"Excavator","Site ID":"S004","Check-Out Date":"2025-01-18","Check-In Date":"2025-03-17","Engine Hours/Day":"4.9","Idle Hours/Day":"6.3","Operating Days":"6","Last Operator ID":"OP110","FuelUsage(L/day)":"39.7"},
    {"Equipment ID":"EQX1031","Type":"Grader","Site ID":"S009","Check-Out Date":"2025-01-04","Check-In Date":"2025-01-28","Engine Hours/Day":"2.8","Idle Hours/Day":"4.5","Operating Days":"18","Last Operator ID":"OP105","FuelUsage(L/day)":"40.8"},
    {"Equipment ID":"EQX1032","Type":"Excavator","Site ID":"S001","Check-Out Date":"2025-03-08","Check-In Date":"2025-03-26","Engine Hours/Day":"3.4","Idle Hours/Day":"10.2","Operating Days":"15","Last Operator ID":"OP107","FuelUsage(L/day)":"29.5"},
    {"Equipment ID":"EQX1033","Type":"Crane","Site ID":"S007","Check-Out Date":"2025-02-24","Check-In Date":"2025-04-03","Engine Hours/Day":"4.9","Idle Hours/Day":"0.9","Operating Days":"25","Last Operator ID":"OP108","FuelUsage(L/day)":"34.0"},
    {"Equipment ID":"EQX1034","Type":"Dump Truck","Site ID":"S006","Check-Out Date":"2025-03-20","Check-In Date":"2025-05-16","Engine Hours/Day":"6.8","Idle Hours/Day":"5.7","Operating Days":"16","Last Operator ID":"OP102","FuelUsage(L/day)":"49.3"}
];

const ProviderDashboard = () => {
    const [filteredData, setFilteredData] = useState(equipmentData);
    const [searchQuery, setSearchQuery] = useState('');
    const [selectedType, setSelectedType] = useState('');
    const [selectedSiteId, setSelectedSiteId] = useState('');
    const [sortColumn, setSortColumn] = useState('');
    const [sortDirection, setSortDirection] = useState('asc');
    const chartRef1 = useRef(null);
    const chartRef2 = useRef(null);

    useEffect(() => {
        let processedData = [...equipmentData];
        if (searchQuery) {
            processedData = processedData.filter(item =>
                item['Equipment ID'].toLowerCase().includes(searchQuery.toLowerCase()) ||
                item['Type'].toLowerCase().includes(searchQuery.toLowerCase()) ||
                item['Site ID'].toLowerCase().includes(searchQuery.toLowerCase())
            );
        }
        if (selectedType) {
            processedData = processedData.filter(item => item['Type'] === selectedType);
        }
        if (selectedSiteId) {
            processedData = processedData.filter(item => item['Site ID'] === selectedSiteId);
        }
        if (sortColumn) {
            processedData = processedData.sort((a, b) => {
                let valA = a[sortColumn];
                let valB = b[sortColumn];
                if (sortColumn.includes('Date')) {
                    valA = new Date(valA);
                    valB = new Date(valB);
                } else if (!isNaN(parseFloat(valA)) && !isNaN(parseFloat(valB))) {
                    valA = parseFloat(valA);
                    valB = parseFloat(valB);
                } else {
                    valA = String(valA).toLowerCase();
                    valB = String(valB).toLowerCase();
                }
                if (valA < valB) return sortDirection === 'asc' ? -1 : 1;
                if (valA > valB) return sortDirection === 'asc' ? 1 : -1;
                return 0;
            });
        }
        setFilteredData(processedData);
        renderCharts(processedData);
    }, [searchQuery, selectedType, selectedSiteId, sortColumn, sortDirection]);

    const renderCharts = (data) => {
        const usageByType = data.reduce((acc, item) => {
            acc[item['Type']] = (acc[item['Type']] || 0) + 1;
            return acc;
        }, {});
        const usageLabels = Object.keys(usageByType);
        const usageValues = Object.values(usageByType);

        const idleCategories = { 'Low (0-2h)': 0, 'Medium (2-4h)': 0, 'High (>4h)': 0 };
        data.forEach(item => {
            const idle = parseFloat(item['Idle Hours/Day'] || 0);
            if (idle <= 2) { idleCategories['Low (0-2h)']++; }
            else if (idle <= 4) { idleCategories['Medium (2-4h)']++; }
            else { idleCategories['High (>4h)']++; }
        });
        const idleLabels = Object.keys(idleCategories);
        const idleValues = Object.values(idleCategories);

        if (chartRef1.current) {
            if (chartRef1.current.chart) {
                chartRef1.current.chart.destroy();
            }
            const ctx1 = chartRef1.current.getContext('2d');
            chartRef1.current.chart = new Chart(ctx1, {
                type: 'bar',
                data: {
                    labels: usageLabels,
                    datasets: [{
                        label: 'Number of Machines',
                        data: usageValues,
                        backgroundColor: ['rgba(255, 99, 132, 0.6)', 'rgba(54, 162, 235, 0.6)', 'rgba(255, 206, 86, 0.6)', 'rgba(75, 192, 192, 0.6)', 'rgba(153, 102, 255, 0.6)', 'rgba(255, 159, 64, 0.6)'],
                        borderColor: ['rgba(255, 99, 132, 1)', 'rgba(54, 162, 235, 1)', 'rgba(255, 206, 86, 1)', 'rgba(75, 192, 192, 1)', 'rgba(153, 102, 255, 1)', 'rgba(255, 159, 64, 1)'],
                        borderWidth: 1,
                    }],
                },
                options: { responsive: true, maintainAspectRatio: false, scales: { y: { beginAtZero: true }, x: {} } },
            });
        }

        if (chartRef2.current) {
            if (chartRef2.current.chart) {
                chartRef2.current.chart.destroy();
            }
            const ctx2 = chartRef2.current.getContext('2d');
            chartRef2.current.chart = new Chart(ctx2, {
                type: 'pie',
                data: {
                    labels: idleLabels,
                    datasets: [{
                        data: idleValues,
                        backgroundColor: ['rgba(75, 192, 192, 0.6)', 'rgba(255, 206, 86, 0.6)', 'rgba(255, 99, 132, 0.6)'],
                        borderColor: ['rgba(75, 192, 192, 1)', 'rgba(255, 206, 86, 1)', 'rgba(255, 99, 132, 1)'],
                        borderWidth: 1,
                    }],
                },
                options: { responsive: true, maintainAspectRatio: false },
            });
        }
    };

    const handleSort = (column) => {
        if (sortColumn === column) {
            setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc');
        } else {
            setSortColumn(column);
            setSortDirection('asc');
        }
    };

    const getSortIcon = (column) => {
        if (sortColumn === column) {
            return sortDirection === 'asc' ? 'fa-sort-up' : 'fa-sort-down';
        }
        return 'fa-sort';
    };

    const totalMachines = filteredData.length;
    const totalIdleHours = filteredData.reduce((sum, item) => sum + parseFloat(item['Idle Hours/Day'] || 0), 0).toFixed(2);
    const totalEngineHours = filteredData.reduce((sum, item) => sum + parseFloat(item['Engine Hours/Day'] || 0), 0);
    const totalOperatingDays = filteredData.reduce((sum, item) => sum + parseFloat(item['Operating Days'] || 0), 0);
    const totalPossibleHours = totalOperatingDays * 24;
    const utilizationPercentage = totalPossibleHours > 0 ? ((totalEngineHours / totalPossibleHours) * 100).toFixed(2) : 0;
    
    const styles = {
        body: { fontFamily: 'Inter, sans-serif' },
        container: { maxWidth: '1024px', margin: 'auto' },
        h1: { fontSize: '2.25rem', fontWeight: 'bold', textAlign: 'center', marginBottom: '2rem', color: '#1a202c' },
        dashboardContent: { padding: '1rem', maxWidth: '1024px', margin: 'auto' },
        cardsContainer: { display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '1.5rem', marginBottom: '2rem' },
        card: { backgroundColor: 'white', padding: '1.5rem', borderRadius: '1rem', boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)', display: 'flex', alignItems: 'center', justifyContent: 'space-between' },
        cardText: { color: '#a0aec0', fontSize: '1.125rem' },
        cardValue: { fontSize: '2.25rem', fontWeight: 'bold', color: '#2b6cb0', marginTop: '0.25rem' },
        redCardValue: { fontSize: '2.25rem', fontWeight: 'bold', color: '#e53e3e', marginTop: '0.25rem' },
        greenCardValue: { fontSize: '2.25rem', fontWeight: 'bold', color: '#38a169', marginTop: '0.25rem' },
        filtersContainer: { backgroundColor: 'white', padding: '1.5rem', borderRadius: '1rem', boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)', marginBottom: '2rem' },
        filtersGrid: { display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '1rem' },
        input: { padding: '0.75rem', border: '1px solid #cbd5e0', borderRadius: '0.5rem', transition: '0.2s' },
        select: { padding: '0.75rem', border: '1px solid #cbd5e0', borderRadius: '0.5rem', transition: '0.2s' },
        tableContainer: { backgroundColor: 'white', padding: '1.5rem', borderRadius: '1rem', boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)', marginBottom: '2rem' },
        tableWrapper: { overflowX: 'auto', borderRadius: '0.5rem', border: '1px solid #e2e8f0' },
        tableHeader: { backgroundColor: '#f7fafc' },
        th: { padding: '0.75rem 1.5rem', textAlign: 'left', fontSize: '0.75rem', fontWeight: '500', color: '#718096', textTransform: 'uppercase' },
        chartsContainer: { display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', gap: '1.5rem', marginBottom: '2rem' },
        chartCard: { backgroundColor: 'white', padding: '1.5rem', borderRadius: '1rem', boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)' },
    };

    return (
        <div style={styles.body}>
            <div style={styles.container}>
                <h1 style={styles.h1}>Smart Rental Tracking Dashboard 📊</h1>
                <div style={styles.dashboardContent}>
                    <div style={styles.cardsContainer}>
                        <div style={styles.card}>
                            <div>
                                <p style={styles.cardText}>Total Rented Machines</p>
                                <p style={styles.cardValue}>{totalMachines}</p>
                            </div>
                            <i className="fas fa-truck-moving" style={{ color: '#90cdf4', fontSize: '3rem', opacity: '0.3' }}></i>
                        </div>
                        <div style={styles.card}>
                            <div>
                                <p style={styles.cardText}>Total Idle Hours</p>
                                <p style={styles.redCardValue}>{totalIdleHours}</p>
                            </div>
                            <i className="fas fa-hourglass-half" style={{ color: '#fc8181', fontSize: '3rem', opacity: '0.3' }}></i>
                        </div>
                        <div style={styles.card}>
                            <div>
                                <p style={styles.cardText}>Utilization Percentage</p>
                                <p style={styles.greenCardValue}>{utilizationPercentage}%</p>
                            </div>
                            <i className="fas fa-chart-line" style={{ color: '#68d391', fontSize: '3rem', opacity: '0.3' }}></i>
                        </div>
                    </div>
                    <div style={styles.filtersContainer}>
                        <h2 style={{ fontSize: '1.5rem', fontWeight: '600', color: '#4a5568', marginBottom: '1rem' }}>Filter & Search 🔎</h2>
                        <div style={styles.filtersGrid}>
                            <input
                                type="text"
                                placeholder="Search by Equipment ID"
                                value={searchQuery}
                                onChange={(e) => setSearchQuery(e.target.value)}
                                style={styles.input}
                            />
                            <select
                                value={selectedType}
                                onChange={(e) => setSelectedType(e.target.value)}
                                style={styles.select}
                            >
                                <option value="">All Types</option>
                                {[...new Set(equipmentData.map(item => item['Type']))].map(type => (
                                    <option key={type} value={type}>{type}</option>
                                ))}
                            </select>
                            <select
                                value={selectedSiteId}
                                onChange={(e) => setSelectedSiteId(e.target.value)}
                                style={styles.select}
                            >
                                <option value="">All Site IDs</option>
                                {[...new Set(equipmentData.map(item => item['Site ID']))].map(id => (
                                    <option key={id} value={id}>{id}</option>
                                ))}
                            </select>
                        </div>
                    </div>
                    <div style={styles.tableContainer}>
                        <h2 style={{ fontSize: '1.5rem', fontWeight: '600', color: '#4a5568', marginBottom: '1rem' }}>Rented Equipment 🏗</h2>
                        <div style={styles.tableWrapper}>
                            <table style={{ minWidth: '100%', borderCollapse: 'collapse' }}>
                                <thead style={styles.tableHeader}>
                                    <tr>
                                        {['Equipment ID', 'Type', 'Site ID', 'Check-Out Date', 'Check-In Date', 'Engine Hours/Day', 'Idle Hours/Day', 'Operating Days', 'Last Operator ID'].map(header => (
                                            <th
                                                key={header}
                                                onClick={() => handleSort(header)}
                                                style={{ padding: '0.75rem 1.5rem', textAlign: 'left', fontSize: '0.75rem', fontWeight: '500', color: '#718096', textTransform: 'uppercase', cursor: 'pointer' }}
                                            >
                                                {header} <i className={`fas ${getSortIcon(header)}`} style={{ marginLeft: '0.25rem' }}></i>
                                            </th>
                                        ))}
                                    </tr>
                                </thead>
                                <tbody>
                                    {filteredData.map((item, index) => {
                                        const checkInDate = new Date(item['Check-In Date']);
                                        const today = new Date();
                                        const daysUntilReturn = Math.ceil((checkInDate.getTime() - today.getTime()) / (1000 * 60 * 60 * 24));
                                        let rowStyle = { backgroundColor: 'white' };
                                        if (daysUntilReturn < 0) {
                                            rowStyle.backgroundColor = '#fff5f5';
                                        } else if (daysUntilReturn >= 0 && daysUntilReturn <= 3) {
                                            rowStyle.backgroundColor = '#fffdf0';
                                        }
                                        const idleHoursStyle = parseFloat(item['Idle Hours/Day']) > 4 ? { backgroundColor: '#fed7d7', fontWeight: '600', color: '#9b2c2c' } : {};
                                        return (
                                            <tr key={index} style={rowStyle}>
                                                <td style={{ padding: '1rem', whiteSpace: 'nowrap', fontSize: '0.875rem', fontWeight: '500', color: '#1a202c' }}>{item['Equipment ID']}</td>
                                                <td style={{ padding: '1rem', whiteSpace: 'nowrap', fontSize: '0.875rem', color: '#4a5568' }}>{item['Type']}</td>
                                                <td style={{ padding: '1rem', whiteSpace: 'nowrap', fontSize: '0.875rem', color: '#4a5568' }}>{item['Site ID']}</td>
                                                <td style={{ padding: '1rem', whiteSpace: 'nowrap', fontSize: '0.875rem', color: '#4a5568' }}>{item['Check-Out Date']}</td>
                                                <td style={{ padding: '1rem', whiteSpace: 'nowrap', fontSize: '0.875rem', color: '#4a5568' }}>{item['Check-In Date']}</td>
                                                <td style={{ padding: '1rem', whiteSpace: 'nowrap', fontSize: '0.875rem', color: '#4a5568' }}>{item['Engine Hours/Day']}</td>
                                                <td style={{ padding: '1rem', whiteSpace: 'nowrap', fontSize: '0.875rem', color: '#4a5568', ...idleHoursStyle }}>{item['Idle Hours/Day']}</td>
                                                <td style={{ padding: '1rem', whiteSpace: 'nowrap', fontSize: '0.875rem', color: '#4a5568' }}>{item['Operating Days']}</td>
                                                <td style={{ padding: '1rem', whiteSpace: 'nowrap', fontSize: '0.875rem', color: '#4a5568' }}>{item['Last Operator ID']}</td>
                                            </tr>
                                        );
                                    })}
                                </tbody>
                            </table>
                        </div>
                    </div>
                    <div style={styles.chartsContainer}>
                        <div style={styles.chartCard}>
                            <h2 style={{ fontSize: '1.5rem', fontWeight: '600', color: '#4a5568', marginBottom: '1rem' }}>Equipment Usage by Type 📊</h2>
                            <div style={{ position: 'relative', height: '16rem' }}>
                                <canvas ref={chartRef1}></canvas>
                            </div>
                        </div>
                        <div style={styles.chartCard}>
                            <h2 style={{ fontSize: '1.5rem', fontWeight: '600', color: '#4a5568', marginBottom: '1rem' }}>Idle Hours Distribution 📉</h2>
                            <div style={{ position: 'relative', height: '16rem' }}>
                                <canvas ref={chartRef2}></canvas>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ProviderDashboard;
