import React, { useEffect, useState } from 'react';

const equipmentData = [
    // Example data; replace with fetch logic or props as needed
    {"Equipment ID":"EQX1000","Type":"Excavator","Site ID":"S008","Check-Out Date":"2025-01-07","Check-In Date":"2025-01-31","Engine Hours/Day":"6.4","Idle Hours/Day":"12.0","Operating Days":"34","Last Operator ID":"OP110","FuelUsage(L/day)":"37.9"},
    {"Equipment ID":"EQX1001","Type":"Grader","Site ID":"S003","Check-Out Date":"2025-05-08","Check-In Date":"2025-05-13","Engine Hours/Day":"3.6","Idle Hours/Day":"7.7","Operating Days":"34","Last Operator ID":"OP108","FuelUsage(L/day)":"33.1"},
    // ...add more rows or fetch from backend
];

const getCurrentUserId = () => {
    return localStorage.getItem('dashboardUsername') || '';
};

function filterUserData(data, userId) {
    return data.filter(item => item['Last Operator ID'] === userId);
}

function getUpcomingReturns(userData) {
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    return userData.filter(item => {
        const checkInDate = new Date(item['Check-In Date']);
        checkInDate.setHours(0, 0, 0, 0);
        const daysUntilReturn = Math.ceil((checkInDate.getTime() - today.getTime()) / (1000 * 60 * 60 * 24));
        return daysUntilReturn >= 0 && daysUntilReturn <= 5;
    });
}

const UserDashboard = () => {
    const [userId, setUserId] = useState(getCurrentUserId());
    const [userData, setUserData] = useState([]);
    const [upcomingReturns, setUpcomingReturns] = useState([]);

    useEffect(() => {
        const filtered = filterUserData(equipmentData, userId);
        setUserData(filtered);
        setUpcomingReturns(getUpcomingReturns(filtered));
    }, [userId]);

    return (
        <div className="max-w-7xl mx-auto p-4 sm:p-6 lg:p-8 bg-gray-100 min-h-screen">
            <h1 className="text-4xl font-bold text-gray-800 mb-8 text-center">Smart Rental Tracking Dashboard 📊</h1>
            {upcomingReturns.length > 0 && (
                <div className="bg-yellow-100 border-l-4 border-yellow-500 text-yellow-700 p-4 rounded-lg mb-6 shadow-md flex items-center">
                    <i className="fas fa-exclamation-triangle mr-3 text-2xl"></i>
                    <div>
                        <p className="font-bold text-lg">Upcoming Returns!</p>
                        <p className="text-sm">
                            Heads up! The following machines have return dates approaching: {upcomingReturns.map(item => `Equipment ID: ${item['Equipment ID']} (Type: ${item['Type']}, Returns: ${item['Check-In Date']})`).join(', ')}
                        </p>
                    </div>
                </div>
            )}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
                <div className="bg-white p-6 rounded-2xl shadow-lg flex items-center justify-between transition-transform transform hover:scale-105">
                    <div>
                        <p className="text-gray-500 text-lg">My Current Rentals</p>
                        <p className="text-4xl font-bold text-blue-600 mt-1">{userData.length}</p>
                    </div>
                    <i className="fas fa-toolbox text-blue-400 text-5xl opacity-30"></i>
                </div>
                <div className="bg-white p-6 rounded-2xl shadow-lg flex items-center justify-between transition-transform transform hover:scale-105">
                    <div>
                        <p className="text-gray-500 text-lg">Upcoming Returns (5 Days)</p>
                        <p className="text-4xl font-bold text-yellow-600 mt-1">{upcomingReturns.length}</p>
                    </div>
                    <i className="fas fa-calendar-alt text-yellow-400 text-5xl opacity-30"></i>
                </div>
            </div>
            <div className="bg-white p-6 rounded-2xl shadow-lg mb-8">
                <h3 className="text-2xl font-semibold text-gray-700 mb-4">My Rental Details</h3>
                <div className="overflow-x-auto rounded-lg border border-gray-200">
                    <table className="min-w-full divide-y divide-gray-200">
                        <thead className="bg-gray-50">
                            <tr>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Equipment ID</th>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Type</th>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Site ID</th>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Check-Out Date</th>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Check-In Date</th>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Operating Days</th>
                            </tr>
                        </thead>
                        <tbody className="bg-white divide-y divide-gray-200">
                            {userData.map((item, idx) => {
                                const today = new Date();
                                today.setHours(0, 0, 0, 0);
                                const checkInDate = new Date(item['Check-In Date']);
                                checkInDate.setHours(0, 0, 0, 0);
                                const daysUntilReturn = Math.ceil((checkInDate.getTime() - today.getTime()) / (1000 * 60 * 60 * 24));
                                let rowClass = 'hover:bg-gray-50';
                                if (daysUntilReturn < 0) {
                                    rowClass = 'bg-red-50 hover:bg-red-100';
                                } else if (daysUntilReturn >= 0 && daysUntilReturn <= 3) {
                                    rowClass = 'bg-yellow-50 hover:bg-yellow-100';
                                }
                                return (
                                    <tr key={idx} className={rowClass}>
                                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900 rounded-l-lg">{item['Equipment ID']}</td>
                                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{item['Type']}</td>
                                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{item['Site ID']}</td>
                                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{item['Check-Out Date']}</td>
                                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{item['Check-In Date']}</td>
                                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500 rounded-r-lg">{item['Operating Days']}</td>
                                    </tr>
                                );
                            })}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    );
};

export default UserDashboard;
