import React, { useState } from 'react';

const Login = () => {
    const [username, setUsername] = useState('');
    const [role, setRole] = useState('user');

    const handleLogin = (e) => {
        e.preventDefault();
        if (username) {
            localStorage.setItem('dashboardUsername', username);
            if (role === 'user') {
                window.location.href = 'user.html';
            } else {
                window.location.href = 'provider.html';
            }
        }
    };

    const styles = {
        body: { fontFamily: 'Inter, sans-serif' },
        container: { maxWidth: '1024px', margin: 'auto' },
        h1: { fontSize: '2.25rem', fontWeight: 'bold', textAlign: 'center', marginBottom: '2rem', color: '#1a202c' },
        loginContainer: {
            display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center',
            backgroundColor: 'white', borderRadius: '1rem', boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
            padding: '2rem', marginBottom: '2rem'
        },
        h2: { fontSize: '1.5rem', fontWeight: '600', color: '#4a5568', marginBottom: '1rem' },
        form: { width: '100%', maxWidth: '24rem' },
        formGroup: { marginBottom: '1rem' },
        label: { display: 'block', color: '#4a5568', fontSize: '0.875rem', fontWeight: 'bold', marginBottom: '0.5rem' },
        input: {
            boxShadow: '0 1px 3px rgba(0,0,0,.12), 0 1px 2px rgba(0,0,0,.24)',
            appearance: 'none', border: '1px solid #e2e8f0', borderRadius: '0.25rem',
            width: '100%', padding: '0.5rem 0.75rem', color: '#4a5568',
            lineHeight: '1.25', outline: 'none',
            focus: { outline: 'none', boxShadow: '0 0 0 3px rgba(66, 153, 225, 0.5)' }
        },
        button: {
            backgroundColor: '#4299e1', color: 'white', fontWeight: 'bold', padding: '0.5rem 1rem',
            borderRadius: '0.25rem', outline: 'none',
            hover: { backgroundColor: '#2b6cb0' },
            focus: { outline: 'none', boxShadow: '0 0 0 3px rgba(66, 153, 225, 0.5)' }
        }
    };

    return (
        <div style={styles.body}>
            <div style={styles.container}>
                <h1 style={styles.h1}>Smart Rental Tracking Dashboard 📊</h1>
                <div style={styles.loginContainer}>
                    <h2 style={styles.h2}>Login</h2>
                    <form onSubmit={handleLogin} style={styles.form}>
                        <div style={styles.formGroup}>
                            <label style={styles.label} htmlFor="username">Username</label>
                            <input
                                id="username"
                                type="text"
                                required
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                                style={styles.input}
                                placeholder="Enter your username"
                            />
                        </div>
                        <div style={styles.formGroup}>
                            <label style={styles.label} htmlFor="role">Role</label>
                            <select
                                id="role"
                                required
                                value={role}
                                onChange={(e) => setRole(e.target.value)}
                                style={styles.input}
                            >
                                <option value="user">User</option>
                                <option value="provider">Provider</option>
                            </select>
                        </div>
                        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                            <button type="submit" style={styles.button}>Login</button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default Login;
