import React, { useEffect, useState } from 'react';

const equipmentData = [
    // Example data; replace with fetch logic or props as needed
    {"Equipment ID":"EQX1000","Type":"Excavator","Site ID":"S008","Check-Out Date":"2025-01-07","Check-In Date":"2025-01-31","Engine Hours/Day":"6.4","Idle Hours/Day":"12.0","Operating Days":"34","Last Operator ID":"OP110","FuelUsage(L/day)":"37.9"},
    {"Equipment ID":"EQX1001","Type":"Grader","Site ID":"S003","Check-Out Date":"2025-05-08","Check-In Date":"2025-05-13","Engine Hours/Day":"3.6","Idle Hours/Day":"7.7","Operating Days":"34","Last Operator ID":"OP108","FuelUsage(L/day)":"33.1"},
    // ...add more rows or fetch from backend
];

const getCurrentUserId = () => {
    return localStorage.getItem('dashboardUsername') || '';
};

function filterUserData(data, userId) {
    return data.filter(item => item['Last Operator ID'] === userId);
}

function getUpcomingReturns(userData) {
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    return userData.filter(item => {
        const checkInDate = new Date(item['Check-In Date']);
        checkInDate.setHours(0, 0, 0, 0);
        const daysUntilReturn = Math.ceil((checkInDate.getTime() - today.getTime()) / (1000 * 60 * 60 * 24));
        return daysUntilReturn >= 0 && daysUntilReturn <= 5;
    });
}

const UserDashboard = () => {
    const [userId, setUserId] = useState(getCurrentUserId());
    const [userData, setUserData] = useState([]);
    const [upcomingReturns, setUpcomingReturns] = useState([]);

    useEffect(() => {
        const filtered = filterUserData(equipmentData, userId);
        setUserData(filtered);
        setUpcomingReturns(getUpcomingReturns(filtered));
    }, [userId]);

    // Inline styles
    const containerStyle = {
        maxWidth: '1100px',
        margin: '0 auto',
        padding: '32px',
        background: '#f3f4f6',
        minHeight: '100vh',
        fontFamily: 'Inter, sans-serif'
    };
    const cardStyle = {
        background: '#fff',
        padding: '24px',
        borderRadius: '18px',
        boxShadow: '0 2px 8px rgba(0,0,0,0.07)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        marginBottom: '24px',
        transition: 'transform 0.2s',
    };
    const cardTextStyle = {
        color: '#6b7280',
        fontSize: '18px',
    };
    const cardNumberStyle = {
        fontSize: '32px',
        fontWeight: 'bold',
        marginTop: '4px',
        color: '#2563eb',
    };
    const cardNumberYellow = {
        ...cardNumberStyle,
        color: '#ca8a04',
    };
    const notificationStyle = {
        background: '#fef3c7',
        borderLeft: '4px solid #f59e42',
        color: '#b45309',
        padding: '16px',
        borderRadius: '12px',
        marginBottom: '24px',
        boxShadow: '0 2px 8px rgba(0,0,0,0.07)',
        display: 'flex',
        alignItems: 'center',
    };
    const tableContainerStyle = {
        background: '#fff',
        padding: '24px',
        borderRadius: '18px',
        boxShadow: '0 2px 8px rgba(0,0,0,0.07)',
        marginBottom: '32px',
    };
    const tableStyle = {
        width: '100%',
        borderCollapse: 'collapse',
    };
    const thStyle = {
        padding: '12px 24px',
        textAlign: 'left',
        fontSize: '13px',
        fontWeight: '600',
        color: '#6b7280',
        background: '#f9fafb',
        textTransform: 'uppercase',
        borderBottom: '1px solid #e5e7eb',
    };
    const tdStyle = {
        padding: '12px 24px',
        fontSize: '15px',
        color: '#374151',
        borderBottom: '1px solid #e5e7eb',
    };
    const rowRed = { background: '#fee2e2' };
    const rowYellow = { background: '#fef9c3' };

    return (
        <div style={containerStyle}>
            <h1 style={{ fontSize: '32px', fontWeight: 'bold', color: '#1f2937', marginBottom: '32px', textAlign: 'center' }}>
                Smart Rental Tracking Dashboard 📊
            </h1>
            {upcomingReturns.length > 0 && (
                <div style={notificationStyle}>
                    <span style={{ fontSize: '24px', marginRight: '16px' }}>⚠️</span>
                    <div>
                        <p style={{ fontWeight: 'bold', fontSize: '18px' }}>Upcoming Returns!</p>
                        <p style={{ fontSize: '14px' }}>
                            Heads up! The following machines have return dates approaching: {upcomingReturns.map(item => `Equipment ID: ${item['Equipment ID']} (Type: ${item['Type']}, Returns: ${item['Check-In Date']})`).join(', ')}
                        </p>
                    </div>
                </div>
            )}
            <div style={{ display: 'flex', gap: '24px', marginBottom: '32px', flexWrap: 'wrap' }}>
                <div style={cardStyle}>
                    <div>
                        <p style={cardTextStyle}>My Current Rentals</p>
                        <p style={cardNumberStyle}>{userData.length}</p>
                    </div>
                    <span style={{ fontSize: '40px', color: '#60a5fa', opacity: 0.3 }}>🧰</span>
                </div>
                <div style={cardStyle}>
                    <div>
                        <p style={cardTextStyle}>Upcoming Returns (5 Days)</p>
                        <p style={cardNumberYellow}>{upcomingReturns.length}</p>
                    </div>
                    <span style={{ fontSize: '40px', color: '#facc15', opacity: 0.3 }}>📅</span>
                </div>
            </div>
            <div style={tableContainerStyle}>
                <h3 style={{ fontSize: '22px', fontWeight: '600', color: '#374151', marginBottom: '16px' }}>My Rental Details</h3>
                <div style={{ overflowX: 'auto', borderRadius: '12px', border: '1px solid #e5e7eb' }}>
                    <table style={tableStyle}>
                        <thead>
                            <tr>
                                <th style={thStyle}>Equipment ID</th>
                                <th style={thStyle}>Type</th>
                                <th style={thStyle}>Site ID</th>
                                <th style={thStyle}>Check-Out Date</th>
                                <th style={thStyle}>Check-In Date</th>
                                <th style={thStyle}>Operating Days</th>
                            </tr>
                        </thead>
                        <tbody>
                            {userData.map((item, idx) => {
                                const today = new Date();
                                today.setHours(0, 0, 0, 0);
                                const checkInDate = new Date(item['Check-In Date']);
                                checkInDate.setHours(0, 0, 0, 0);
                                const daysUntilReturn = Math.ceil((checkInDate.getTime() - today.getTime()) / (1000 * 60 * 60 * 24));
                                let rowStyle = {};
                                if (daysUntilReturn < 0) {
                                    rowStyle = rowRed;
                                } else if (daysUntilReturn >= 0 && daysUntilReturn <= 3) {
                                    rowStyle = rowYellow;
                                }
                                return (
                                    <tr key={idx} style={rowStyle}>
                                        <td style={tdStyle}>{item['Equipment ID']}</td>
                                        <td style={tdStyle}>{item['Type']}</td>
                                        <td style={tdStyle}>{item['Site ID']}</td>
                                        <td style={tdStyle}>{item['Check-Out Date']}</td>
                                        <td style={tdStyle}>{item['Check-In Date']}</td>
                                        <td style={tdStyle}>{item['Operating Days']}</td>
                                    </tr>
                                );
                            })}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    );
};

export default UserDashboard;
