import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

// Predefined user credentials for demonstration purposes
// In a real application, these would be stored securely on a backend server.
const userCredentials = [
    { username: 'user1', password: 'password123', role: 'user' },
    { username: 'provider1', password: 'adminpassword', role: 'provider' },
    { username: 'op101', password: 'op101pass', role: 'user' }, // Example operator
    { username: 'op102', password: 'op102pass', role: 'user' }, // Example operator
    { username: 'op103', password: 'op103pass', role: 'user' }, // Example operator
    { username: 'op104', password: 'op104pass', role: 'user' }, // Example operator
    
];

const Login = () => {
    const navigate = useNavigate();
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState(''); // New state for password
    const [role, setRole] = useState('user');
    const [loginError, setLoginError] = useState(''); // New state for login error messages

    const handleLogin = (e) => {
        e.preventDefault();
        setLoginError(''); // Clear any previous error messages

        // Find a matching user in the predefined credentials
        const foundUser = userCredentials.find(
            (user) => user.username === username && user.password === password && user.role === role
        );

        if (foundUser) {
            // If credentials are valid, store username and navigate
            localStorage.setItem('dashboardUsername', foundUser.username);
            if (foundUser.role === 'user') {
                navigate('/user');
            } else {
                navigate('/provider');
            }
        } else {
            // If credentials are invalid, display an error message
            setLoginError('Invalid username, password, or role. Please try again.');
        }
    };

    const styles = {
        body: { fontFamily: 'Inter, sans-serif' },
        container: { maxWidth: '1024px', margin: 'auto' },
        h1: { fontSize: '2.25rem', fontWeight: 'bold', textAlign: 'center', marginBottom: '2rem', color: '#1a202c' },
        loginContainer: {
            display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center',
            backgroundColor: 'white', borderRadius: '1rem', boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
            padding: '2rem', marginBottom: '2rem'
        },
        h2: { fontSize: '1.5rem', fontWeight: '600', color: '#4a5568', marginBottom: '1rem' },
        form: { width: '100%', maxWidth: '24rem' },
        formGroup: { marginBottom: '1rem' },
        label: { display: 'block', color: '#4a5568', fontSize: '0.875rem', fontWeight: 'bold', marginBottom: '0.5rem' },
        input: {
            boxShadow: '0 1px 3px rgba(0,0,0,.12), 0 1px 2px rgba(0,0,0,.24)',
            appearance: 'none', border: '1px solid #e2e8f0', borderRadius: '0.25rem',
            width: '100%', padding: '0.5rem 0.75rem', color: '#4a5568',
            lineHeight: '1.25', outline: 'none',
        },
        button: {
            backgroundColor: '#4299e1', color: 'white', fontWeight: 'bold', padding: '0.5rem 1rem',
            borderRadius: '0.25rem', outline: 'none',
        },
        errorMessage: {
            color: '#e53e3e',
            fontSize: '0.875rem',
            marginTop: '0.5rem',
            textAlign: 'center'
        }
    };

    return (
        <div style={styles.body}>
            <div style={styles.container}>
                <h1 style={styles.h1}>Smart Rental Tracking Dashboard 📊</h1>
                <div style={styles.loginContainer}>
                    <h2 style={styles.h2}>Login</h2>
                    <form onSubmit={handleLogin} style={styles.form}>
                        <div style={styles.formGroup}>
                            <label style={styles.label} htmlFor="username">Username</label>
                            <input
                                id="username"
                                type="text"
                                required
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                                style={styles.input}
                                placeholder="Enter your username"
                            />
                        </div>
                        <div style={styles.formGroup}>
                            <label style={styles.label} htmlFor="password">Password</label>
                            <input
                                id="password"
                                type="password" // Changed type to password
                                required
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                style={styles.input}
                                placeholder="Enter your password"
                            />
                        </div>
                        <div style={styles.formGroup}>
                            <label style={styles.label} htmlFor="role">Role</label>
                            <select
                                id="role"
                                required
                                value={role}
                                onChange={(e) => setRole(e.target.value)}
                                style={styles.input}
                            >
                                <option value="user">User</option>
                                <option value="provider">Provider</option>
                            </select>
                        </div>
                        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', flexDirection: 'column' }}>
                            <button type="submit" style={styles.button}>Login</button>
                            {loginError && <p style={styles.errorMessage}>{loginError}</p>}
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default Login;
